package com.ottd.libs.ui;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.widget.TextView;import android.widget.Toast;import com.ottd.libs.thread.ThreadManager;public class ToastUtil {    public static void show(final Context ctx, final String toastInfo, final int duration){        View contentView = LayoutInflater.from(ctx).inflate(R.layout.com_bihe0832_common_toast, null);        final View layout = contentView.findViewById(R.id.bihe0832_common_custom_toast_layout_id);        TextView toastText = (TextView) layout.findViewById(R.id.bihe0832_common_toastText);        toastText.setText(toastInfo);        ThreadManager.getInstance().runOnUIThread(new Runnable() {            @Override            public void run() {                Toast toast = new Toast(ctx);                toast.setDuration(duration);                toast.setView(layout);                toast.show();            }        });    }    public static void showLong(final Context ctx,final String toastInfo){        show(ctx,toastInfo,Toast.LENGTH_LONG);    }    public static void showShort(final Context ctx,final String toastInfo){        show(ctx,toastInfo,Toast.LENGTH_SHORT);    }  }  