package com.bihe0832.readhub.module.readhub.news;import com.bihe0832.readhub.libware.util.TextUtils;import com.bihe0832.readhub.module.readhub.topic.TopicInfoDetail;import java.util.ArrayList;/** * 主UI展示的项目的结构体 * @author hardyshi * */public class NewsInfo {	public String mId = "";	public String mTitle = "";	public String mSummary = "";	public String mPublishDate = "";	public String mAuthorName = "";	public String mSiteName = "";	public String mUrl = "";	public NewsInfo(){	}	public String getmTitle() {		return mTitle;	}	public String getmSummary() {		if(TextUtils.ckIsEmpty(mSummary)){			return "";		}else{			return mSummary;		}	}}