package com.bihe0832.readhub.module.readhub.news.request;import com.bihe0832.readhub.libware.util.TextUtils;import com.bihe0832.readhub.libware.util.TimeUtils;/** * 主UI展示的项目的结构体 * @author hardyshi * */public class NewsInfo {	protected String mId = "";	protected String mTitle = "";	protected String mSummary = "";	protected String mPublishDate = "";	protected String mAuthorName = "";	protected String mSiteName = "";	protected String mUrl = "";	public NewsInfo(){	}	public String getmTitle() {		return mTitle;	}	public String getmSummary() {		if(TextUtils.ckIsEmpty(mSummary)){			return "";		}else{			return mSummary;		}	}	public String getmId() {		return mId;	}	public String getmPublishDate() {		String date = mPublishDate.replace("T"," ");		int a = date.lastIndexOf(".");		date = date.substring(0, a);		return TimeUtils.getDateCompareResult(date);	}	public String getmAuthorName() {		if(TextUtils.ckIsEmpty(mAuthorName)){			return getmSiteName();		}else{			return mAuthorName;		}	}	public String getmSiteName() {		if(TextUtils.ckIsEmpty(mSiteName)){			return "";		}else{			return mSiteName;		}	}	public String getmUrl() {		return mUrl;	}}