package com.bihe0832.readhub.module.readhub.topic.request;import com.bihe0832.readhub.libware.util.TextUtils;import java.util.ArrayList;/** * 主UI展示的项目的结构体 * @author hardyshi * */public class TopicInfo {	protected String mId = "";	protected String mTitle = "";	protected String mSummary = "";	protected ArrayList<TopicInfoDetail> mNewsArrayList = new ArrayList();	protected String mPublishDate = "";	protected long mOrder = 0;	public TopicInfo(){	}	public String getmTitle() {		return mTitle;	}	public String getmSummary() {		if(TextUtils.ckIsEmpty(mSummary)){			return "";		}else{			return mSummary;		}	}	public ArrayList<TopicInfoDetail> getNewsArrayList() {		return mNewsArrayList;	}}